import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "../css/join.css";
import SubLogo from "../assets/sub-logo.svg";

const Join = () => {
  const navigate = useNavigate();
  const [isDuplicateChecked, setIsDuplicateChecked] = useState(false); // 중복 확인 상태 관리
  const [nickname, setNickname] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [nicknameError, setNicknameError] = useState("");

  // 중복 확인 버튼
  const handleDuplicateCheck = async (nickname) => {
    if (!nickname) {
      setNicknameError("닉네임을 작성해주세요.");
      return false;
    }
    try {
      const response = await axios.get(
        `{REACT_APP_API_BASE_URL}/auth/nick_check/${nickname}`
      );
      if (response.data.message === "success") {
        setNicknameError("");
        setNicknameChecked(true); // 중복 확인 완료
        return true;
      } else if (response.data.message === "already_exist") {
        setNicknameError("중복된 닉네임입니다. 다른 닉네임을 사용해주세요.");
        setNicknameChecked(false);
        return false;
      }
    } catch (error) {
      setNicknameError("닉네임 검사 중 오류 발생" + error);
      setNicknameChecked(false);
      return false;
    }

    setIsDuplicateChecked(true);
  };

  // 회원가입하기
  const handleSubmit = () => {
    // 중복 확인 로직 (추후 백엔드 연동 필요)
    navigate("/");
  };

  return (
    <div className="signup-container">
      <div className="logo">
        <img src={SubLogo} alt="면접MATE" />
        <h3>면접MATE</h3>
      </div>
      <div className="signup-form">
        <h2>회원가입</h2>
        <form>
          <p>이메일</p>
          <input type="email" placeholder="이메일" required />
          <p>닉네임</p>
          <div className="nickname">
            <input type="text" placeholder="닉네임" required />
            <button
              type="button"
              onClick={handleDuplicateCheck}
              className={`duplicate-check-button ${
                isDuplicateChecked ? "checked" : ""
              }`}
            >
              {isDuplicateChecked ? "확인완료" : "중복확인"}
            </button>
          </div>
          <p>비밀번호</p>
          <input type="password" placeholder="비밀번호" required />
          <p>비밀번호 재확인</p>
          <input type="password" placeholder="비밀번호 재확인" required />
          <button
            type="submit"
            className="signup-button"
            onClick={handleSubmit}
          >
            서비스 이용하기
          </button>
        </form>
      </div>
    </div>
  );
};

export default Join;
