import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "../css/join.css";
import SubLogo from "../assets/sub-logo.svg";

const Join = () => {
  const navigate = useNavigate();
  const [isDuplicateChecked, setIsDuplicateChecked] = useState(false); // 중복 확인 상태 관리

  // 중복 확인 버튼 클릭
  const handleDuplicateCheck = () => {
    // 중복 확인 로직 (추후 백엔드 연동 필요)
    setIsDuplicateChecked(true);
  };

  // 회원가입하기
  const handleSubmit = () => {
    navigate("/");
  };

  return (
    <div className="signup-container">
      <div className="signup-form">
        <img src={SubLogo} alt="면접MATE" className="logo" />
        <form>
          <h2>회원가입</h2>
          <input type="email" placeholder="Email" required />
          <div className="nickname">
            <input type="text" placeholder="NickName" required />
            <button
              type="button"
              onClick={handleDuplicateCheck}
              className={`duplicate-check-button ${
                isDuplicateChecked ? "checked" : ""
              }`}
            >
              {isDuplicateChecked ? "확인완료" : "중복확인"}
            </button>
          </div>
          <input type="password" placeholder="Password" required />
          <input type="password" placeholder="Check password" required />
          <button
            type="submit"
            className="signup-button"
            onClick={handleSubmit}
          >
            서비스 이용하기
          </button>
        </form>
      </div>
    </div>
  );
};

export default Join;
