import React from "react";
import { useNavigate } from "react-router-dom";
import "../css/login.css";
import SubLogo from "../assets/sub-logo.svg";
const API_BASE_URL = process.env.REACT_APP_API_BASE_URL;

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [loginError, setLoginError] = useState("");
  const [pwdError, setPwdError] = useState("");
  const navigate = useNavigate();

  const handleLoginClick = async (e) => {
    e.preventDefualt();

    e.preventDefault();
    const data = new FormData(e.currentTarget);
    const email = data.get("email");
    const password = data.get("password");

    if (!email) {
      setLoginError("이메일과 비밀번호를 모두 입력해주세요.");
      return;
    } else setLoginError("");
    if (!password) {
      setPwdError("이메일과 비밀번호를 모두 입력해주세요.");
      return;
    } else setPwdError("");

    try {
      const response = await axios.post("http://localhost:5001/auth/login", {
        email: email,
        password: password,
      });
      if (response.data.message === "incorrect_pw") {
        setPwdError("비밀번호가 틀렸습니다.");
      } else if (response.data.message === "undefined_email")
        setLoginError("이메일이 존재하지 않습니다. 회원가입을 해주세요.");
      else {
        localStorage.setItem("userToken", response.data.token); // 세션 유지를 위한 토큰 저장(서버에서 추가 로직 필요)
        navigate("/app/home");
      }
    } catch (error) {
      setLoginError("서버 오류. 로그인 불가");
    }
    //확인용
    console.log({
      email: data.get("email"),
      password: data.get("password"),
    });
  };

  const handleJoinClick = () => {
    navigate("/join");
  };

  return (
    <div className="login-container">
      <div className="logo">
        <img src={SubLogo} alt="면접MATE" />
        <h3>면접MATE</h3>
      </div>
      <div className="login-form">
        <h2>로그인</h2>
        <form>
          <p>이메일</p>
          <input type="email" placeholder="이메일" required />
          <p>비밀번호</p>
          <input type="password" placeholder="비밀번호" required />
          <button
            type="submit"
            className="login-button"
            onClick={handleLoginClick}
          >
            로그인
          </button>
        </form>
        <div className="divider">
          <hr />
          <span>참여하기</span>
          <hr />
        </div>
        <button className="join-button" onClick={handleJoinClick}>
          회원가입
        </button>
      </div>
    </div>
  );
};

export default Login;
